cat Long/rphost*/*.log | egrep ',CALL,.*Context=.*MemoryPeak=.*InBytes=.*OutBytes=' | sed -r 's/\xef\xbb\xbf//g;s/^[0-9]+:[0-9]+\.[0-9]+-//;s/,CALL.*Context=/|/;s/,.*MemoryPeak=/|/;s/,.*InBytes=/|/;s/,.*OutBytes=/|/;s/\./#/g' | gawk -F '|' '{Dur[$2]+=$1; Exec[$2]+=1; if (Max[$2] < $1) {Max[$2] = $1}; if (Min[$2] == 0 || Min[$2] > $1) {Min[$2] = $1}; Memory[$2]+=$3; if (MaxMem[$2] < $3) {MaxMem[$2] = $3}; InBytes[$2]+=$4; OutBytes[$2]+=$5}END{print "Dur(sec)\tCount\tAvgDur(sec)\tMaxDur(sec)\tMinDur(sec)\tMemory(MB)\tAvgMemory(MB)\tMaxMemory(MB)\tReads(MB)\tWrites(MB)\tContext"; for (i in Dur) {print Dur[i]/1000000 "\t" Exec[i] "\t" Dur[i]/1000000/Exec[i] "\t" Max[i]/1000000 "\t" Min[i]/1000000 "\t" Memory[i]/1024/1024 "\t" Memory[i]/1024/1024/Exec[i] "\t" MaxMem[i]/1024/1024 "\t" InBytes[i]/1024/1024 "\t" OutBytes[i]/1024/1024 "\t" i}}' | sed -r 's/\./,/g;s/#/\./g' > CALL_for_Excell.txt